           提交

 `})
export class AppComponent {
    stateFlag = false;
    toggleState() {
      this.stateFlag = !this.stateFlag;
    }

    submit() {
      console.log('Button submitted');
    }

    calculateClasses() {
      return {
        btn: true,
        'btn-primary': true,
